/**
 * üéØ Error Utilities: Your Error Handling Helper!
 *
 * Makes errors easier to understand and handle.
 * Like having a friendly detective that investigates problems! üîç
 */

/**
 * üé® Custom Error: Your Special Error Type
 *
 * A better way to handle errors in your app.
 * Like having a detailed incident report form! üìù
 *
 * Features:
 * - üè∑Ô∏è Error codes
 * - üìö Stack traces
 * - üîç Original error tracking
 * - üìä JSON formatting
 *
 * @class CustomError
 * @extends {Error}
 */
export class CustomError extends Error {
  readonly code: string;
  readonly originalError?: Error;

  /**
   * üé¨ Create New Error
   *
   * Makes a new custom error with all the details.
   * Like filling out an incident report! üìã
   *
   * @param {string} code - What kind of error it is
   * @param {Error} [originalError] - What caused it
   */
  constructor(code: string, originalError?: Error) {
    super(originalError?.message || code);
    this.code = code;
    this.originalError = originalError;
    this.name = "CustomError";

    // Maintains proper stack trace for where error was thrown
    if (Error.captureStackTrace) {
      Error.captureStackTrace(this, CustomError);
    }
  }

  /**
   * üìù Convert to JSON
   *
   * Makes the error easy to save or send.
   * Like making a clean copy of the incident report! üìÑ
   *
   * @returns {Record<string, unknown>} The error details
   */
  toJSON(): Record<string, unknown> {
    return {
      name: this.name,
      code: this.code,
      message: this.message,
      stack: this.stack,
      originalError: this.originalError
        ? {
            name: this.originalError.name,
            message: this.originalError.message,
            stack: this.originalError.stack,
          }
        : undefined,
    };
  }
}

/**
 * üé® Format Error Message
 *
 * Makes errors look nice and readable.
 * Like making a neat summary of what went wrong! üìã
 *
 * @param {Error} error - The error to format
 * @returns {string} A pretty error message
 */
export function formatError(error: Error): string {
  if (error instanceof CustomError) {
    return JSON.stringify(error.toJSON());
  }
  return JSON.stringify({
    name: error.name,
    message: error.message,
    stack: error.stack,
  });
}

/**
 * üìö Elasticsearch Client: Your Data Librarian!
 *
 * Manages your connection to Elasticsearch.
 * Like having a friendly librarian who helps organize and find your books! üìñ
 *
 * Features:
 * - üîå Connection management
 * - üìù Index creation and deletion
 * - üè• Health checks
 * - üîí Secure authentication
 */

import { CustomError } from "@/lib/cortex/utils/errors";
import type { Logger } from "@/lib/shared/types";
import { Client } from "@elastic/elasticsearch";

/**
 * üìö Elastic Client: Your Library Manager
 *
 * Handles all communication with Elasticsearch.
 * Like having a master librarian who knows every book's location! üó∫Ô∏è
 *
 * @class ElasticClient
 */
export class ElasticClient {
  private readonly client: Client;
  private readonly logger: Logger;

  /**
   * üé¨ Create New Client
   *
   * Sets up your connection to Elasticsearch.
   * Like opening up the library for the day! üåÖ
   *
   * @throws {CustomError} If connection setup fails
   */
  constructor(logger: Logger) {
    this.logger = logger;
    try {
      const config: Record<string, any> = {
        node: process.env.ELASTICSEARCH_URL || "http://localhost:9200",
      };

      // Only add auth if API key exists
      if (process.env.ELASTICSEARCH_API_KEY) {
        config.auth = {
          apiKey: process.env.ELASTICSEARCH_API_KEY,
        };
      }

      this.client = new Client(config);
    } catch (error) {
      this.logger.error("Failed to initialize Elasticsearch client", { error });
      throw error;
    }
  }

  /**
   * üè• Check Connection Health
   *
   * Makes sure we can talk to Elasticsearch.
   * Like checking if the library is open! üö™
   *
   * @returns {Promise<boolean>} true if healthy, false if there's a problem
   */
  async ping(): Promise<boolean> {
    try {
      await this.client.ping();
      this.logger.debug("Elasticsearch ping successful");
      return true;
    } catch (error) {
      const err = error as Error & {
        meta?: { statusCode?: number; headers?: unknown; body?: unknown };
      };
      this.logger.error("Elasticsearch ping failed", {
        error: {
          message: err.message,
          name: err.name,
          statusCode: err.meta?.statusCode,
          headers: err.meta?.headers,
          body: err.meta?.body,
        },
      });
      return false;
    }
  }

  /**
   * üìö Create New Index
   *
   * Creates a new place to store documents.
   * Like adding a new section to the library! üìö
   *
   * @param {string} index - What to call the new section
   * @param {Record<string, unknown>} mappings - How to organize it
   * @throws {CustomError} If creation fails
   */
  async createIndex(
    index: string,
    mappings: Record<string, unknown>
  ): Promise<void> {
    try {
      this.logger.debug(`Checking if index exists: ${index}`);
      const exists = await this.client.indices.exists({ index });
      this.logger.debug(`Index ${index} exists: ${exists}`);

      if (!exists) {
        this.logger.debug(`Creating index: ${index}`, { mappings });
        await this.client.indices.create({
          index,
          body: {
            mappings,
          },
        });
        this.logger.info(`Created index: ${index}`);
      } else {
        this.logger.debug(`Index ${index} already exists`);
      }
    } catch (error) {
      const err = error as Error & {
        meta?: { statusCode?: number; headers?: unknown; body?: unknown };
      };
      this.logger.error(`Failed to create index: ${index}`, {
        error: {
          message: err.message,
          name: err.name,
          statusCode: err.meta?.statusCode,
          headers: err.meta?.headers,
          body: err.meta?.body,
        },
      });
      throw new CustomError("ELASTIC_CREATE_INDEX_FAILED", error as Error);
    }
  }

  /**
   * üóëÔ∏è Delete Index
   *
   * Removes an index and all its documents.
   * Like carefully removing a section from the library! üìö
   *
   * @param {string} index - Which section to remove
   * @throws {CustomError} If deletion fails
   */
  async deleteIndex(index: string): Promise<void> {
    try {
      this.logger.debug(`Checking if index exists: ${index}`);
      const exists = await this.client.indices.exists({ index });
      this.logger.debug(`Index ${index} exists: ${exists}`);

      if (exists) {
        await this.client.indices.delete({ index });
        this.logger.info(`Deleted index: ${index}`);
      } else {
        this.logger.debug(`Index ${index} does not exist`);
      }
    } catch (error) {
      const err = error as Error & {
        meta?: { statusCode?: number; headers?: unknown; body?: unknown };
      };
      this.logger.error(`Failed to delete index: ${index}`, {
        error: {
          message: err.message,
          name: err.name,
          statusCode: err.meta?.statusCode,
          headers: err.meta?.headers,
          body: err.meta?.body,
        },
      });
      throw new CustomError("ELASTIC_DELETE_INDEX_FAILED", error as Error);
    }
  }

  /**
   * üîë Get Client Instance
   *
   * Gets the raw Elasticsearch client.
   * Like getting the master key to the library! üóùÔ∏è
   *
   * @returns {Client} The Elasticsearch client
   */
  getClient(): Client {
    return this.client;
  }
}
