import type { MetricsService } from "@/lib/cortex/monitoring/metrics";
import type { Logger } from "@/lib/shared/types";
import type { ExperimentStatus, PrismaClient } from "@prisma/client";

/**
 * üß™ A/B Test Configuration: Your Experiment Blueprint
 *
 * All the details needed to run a search experiment.
 * Like a recipe for testing different search approaches! üî¨
 *
 * @interface ABTestConfig
 * @property {string} id - Unique test identifier
 * @property {string} name - What we're testing
 * @property {SearchVariant[]} variants - Different versions to test
 * @property {Date} startDate - When to start testing
 * @property {Date} endDate - When to stop testing
 * @property {number} trafficAllocation - How many users to include (0-1)
 */
interface ABTestConfig {
  id: string;
  name: string;
  variants: SearchVariant[];
  startDate: Date;
  endDate: Date;
  trafficAllocation: number;
}

/**
 * üîÑ Search Variant: A Different Way to Search
 *
 * One version of search we want to test.
 * Like trying a different recipe to make the same dish! üç≥
 *
 * @interface SearchVariant
 * @property {string} id - Unique variant identifier
 * @property {string} name - What makes this version special
 * @property {Record<string, number>} weights - How important different factors are
 * @property {number} allocation - How many users should try this version (0-1)
 */
interface SearchVariant {
  id: string;
  name: string;
  weights: Record<string, number>;
  allocation: number;
}

/**
 * üìã A/B Test Record: Test Details in the Database
 *
 * How we store test information.
 * Like keeping a lab notebook of our experiments! üìî
 *
 * @interface ABTestRecord
 */
interface ABTestRecord {
  id: string;
  name: string;
  description?: string;
  startDate: Date;
  endDate: Date;
  status: string;
  configuration: {
    variants: SearchVariant[];
    trafficAllocation: number;
  };
}

/**
 * üß™ Search A/B Testing Service: Your Experiment Lab!
 *
 * This service helps us run controlled experiments to make search better.
 * Think of it as a scientific laboratory for improving search! üî¨
 *
 * Features:
 * - üéØ Runs controlled experiments
 * - üé≤ Fairly assigns users to test groups
 * - üìä Tracks experiment results
 * - üìà Measures improvements
 * - üîÑ Manages test lifecycles
 *
 * @class SearchABTestingService
 */
export class SearchABTestingService {
  private readonly logger: Logger;
  private readonly prisma: PrismaClient;
  private readonly metrics: MetricsService;

  /**
   * üéí Sets Up Your Testing Lab
   *
   * Gets everything ready to run experiments.
   * Like preparing all your lab equipment! üß™
   *
   * @param {Object} config - Everything we need to run tests
   * @param {Logger} config.logger - Our lab notebook
   * @param {PrismaClient} config.prisma - Our data storage
   * @param {MetricsService} config.metrics - Our measuring tools
   */
  constructor(config: {
    logger: Logger;
    prisma: PrismaClient;
    metrics: MetricsService;
  }) {
    this.logger = config.logger;
    this.prisma = config.prisma;
    this.metrics = config.metrics;
  }

  /**
   * üîç Finds an Active Test
   *
   * Looks up details about a running experiment.
   * Like checking what's currently in your test tubes! üß™
   *
   * @private
   * @param {string} testId - Which test to look for
   * @returns {Promise<ABTestConfig>} The test details
   * @throws {Error} If the test isn't found or is invalid
   */
  private async getActiveTest(testId: string): Promise<ABTestConfig> {
    if (!testId) {
      throw new Error("Test ID is required");
    }

    const test = (await this.prisma.aBTest.findFirst({
      where: {
        id: testId,
        status: "ACTIVE",
        startDate: { lte: new Date() },
        endDate: { gte: new Date() },
      },
    })) as unknown as ABTestRecord;

    if (!test) {
      throw new Error(`No active test found for ID: ${testId}`);
    }

    if (
      !test.configuration?.variants ||
      !Array.isArray(test.configuration.variants)
    ) {
      throw new Error(`Invalid test configuration for ID: ${testId}`);
    }

    return {
      ...test,
      variants: test.configuration.variants.map((variant) => ({
        ...variant,
        allocation: variant.allocation || 0,
        weights: variant.weights || {},
      })),
      trafficAllocation: test.configuration.trafficAllocation || 1,
    };
  }

  /**
   * üîé Gets a Specific Test Version
   *
   * Finds details about one version of a test.
   * Like examining one specific sample in your lab! üî¨
   *
   * @private
   * @param {string} testId - Which test to look in
   * @param {string} variantId - Which version to find
   * @returns {Promise<SearchVariant>} The version details
   * @throws {Error} If the version isn't found
   */
  private async getVariant(
    testId: string,
    variantId: string
  ): Promise<SearchVariant> {
    if (!testId || !variantId) {
      throw new Error("Test ID and variant ID are required");
    }

    const test = await this.getActiveTest(testId);
    const variant = test.variants.find((v) => v.id === variantId);

    if (!variant) {
      throw new Error(`Variant ${variantId} not found in test ${testId}`);
    }

    return {
      ...variant,
      allocation: variant.allocation || 0,
      weights: variant.weights || {},
    };
  }

  /**
   * üé≤ Picks a Test Version
   *
   * Fairly chooses which version a user should see.
   * Like randomly selecting a test tube for your experiment! üß™
   *
   * @private
   * @param {ABTestConfig} test - The test to pick from
   * @returns {SearchVariant} The chosen version
   * @throws {Error} If there are no versions to pick from
   */
  private selectVariant(test: ABTestConfig): SearchVariant {
    if (!test.variants || test.variants.length === 0) {
      throw new Error("Test has no variants");
    }

    // Normalize allocations if they don't sum to 1
    const totalAllocation = test.variants.reduce(
      (sum, v) => sum + (v.allocation || 0),
      0
    );
    if (totalAllocation === 0) {
      // If no allocations set, distribute evenly
      const evenAllocation = 1 / test.variants.length;
      test.variants.forEach((v) => (v.allocation = evenAllocation));
    } else if (totalAllocation !== 1) {
      // Normalize to sum to 1
      test.variants.forEach(
        (v) => (v.allocation = (v.allocation || 0) / totalAllocation)
      );
    }

    const random = Math.random();
    let cumulative = 0;

    for (const variant of test.variants) {
      cumulative += variant.allocation;
      if (random <= cumulative) {
        return variant;
      }
    }

    return test.variants[0];
  }

  /**
   * üéØ Assigns a User to a Test Version
   *
   * Decides which version of search a user should see.
   * Like assigning a participant to a test group! üë•
   *
   * @param {string} sessionId - Which user we're assigning
   * @param {string} testId - Which test we're running
   * @returns {Promise<SearchVariant>} The assigned version
   * @throws {Error} If something goes wrong during assignment
   */
  async assignVariant(
    sessionId: string,
    testId: string
  ): Promise<SearchVariant> {
    if (!sessionId || !testId) {
      throw new Error("Session ID and test ID are required");
    }

    try {
      // First check if this session already has an assignment
      const existing = await this.prisma.aBTestAssignment.findFirst({
        where: {
          sessionId,
          testId,
        },
      });

      if (existing) {
        this.logger.debug("Found existing variant assignment", {
          sessionId,
          testId,
          variantId: existing.variantId,
        });
        return this.getVariant(testId, existing.variantId);
      }

      // Get active test configuration
      const test = await this.getActiveTest(testId);

      // Check if user should be included in test based on traffic allocation
      if (Math.random() > test.trafficAllocation) {
        this.logger.debug(
          "Session excluded from test based on traffic allocation",
          {
            sessionId,
            testId,
            allocation: test.trafficAllocation,
          }
        );
        // Return default variant
        return test.variants[0];
      }

      // Select a variant
      const variant = this.selectVariant(test);

      // Create assignment record
      await this.prisma.aBTestAssignment.create({
        data: {
          sessionId,
          testId,
          variantId: variant.id,
          timestamp: new Date(),
        },
      });

      this.logger.debug("Created new variant assignment", {
        sessionId,
        testId,
        variantId: variant.id,
      });

      return variant;
    } catch (error) {
      this.logger.error("Failed to assign test variant", {
        error,
        sessionId,
        testId,
        errorType: error instanceof Error ? error.name : typeof error,
        message: error instanceof Error ? error.message : String(error),
      });
      throw error;
    }
  }

  /**
   * üìä Records Test Results
   *
   * Saves how well each version is performing.
   * Like recording the results of your experiment! üìà
   *
   * @param {Object} data - The test results
   * @param {string} data.testId - Which test these results are for
   * @param {string} data.variantId - Which version these results are for
   * @param {string} data.queryHash - Which search query was tested
   * @param {Object} data.metrics - How well it performed
   * @throws {Error} If something goes wrong while saving
   */
  async trackVariantMetrics(data: {
    testId: string;
    variantId: string;
    queryHash: string;
    metrics: {
      clickThroughRate: number;
      averageRelevance: number;
      conversionRate: number;
    };
  }): Promise<void> {
    try {
      await this.prisma.aBTestMetrics.create({
        data: {
          testId: data.testId,
          variantId: data.variantId,
          queryHash: data.queryHash,
          metrics: data.metrics,
          timestamp: new Date(),
        },
      });
      // Update Prometheus metrics
      this.metrics.updateABTestMetrics({
        test_id: data.testId,
        variant_id: data.variantId,
        query_hash: data.queryHash,
        metrics: {
          clickThroughRate: data.metrics.clickThroughRate,
          averageRelevance: data.metrics.averageRelevance,
          conversionRate: data.metrics.conversionRate,
        },
      });
    } catch (error) {
      this.logger.error("Failed to track variant metrics", { error, data });
      throw error;
    }
  }

  async createTest(params: {
    name: string;
    description?: string;
    startDate: Date;
    endDate: Date;
    status: string;
    configuration: {
      variants: SearchVariant[];
    };
  }): Promise<ABTestRecord> {
    return (await this.prisma.aBTest.create({
      data: {
        name: params.name,
        description: params.description,
        startDate: params.startDate,
        endDate: params.endDate,
        status: params.status as ExperimentStatus,
        configuration: JSON.parse(JSON.stringify(params.configuration)),
      },
    })) as unknown as ABTestRecord;
  }

  async getTestByName(name: string): Promise<ABTestRecord | null> {
    try {
      const test = await this.prisma.aBTest.findFirst({
        where: {
          name,
          status: "ACTIVE",
          startDate: {
            lte: new Date(),
          },
          endDate: {
            gte: new Date(),
          },
        },
      });

      if (!test) {
        this.logger.debug("No active test found with name", { name });
        return null;
      }

      return test as unknown as ABTestRecord;
    } catch (error) {
      this.logger.error("Failed to get test by name", { error, name });
      throw error;
    }
  }

  async trackConversion(data: ConversionEvent): Promise<void> {
    await this.prisma.aBTestMetric.create({
      data: {
        test: {
          connect: { id: data.testId },
        },
        session: {
          connect: { id: data.sessionId },
        },
        variantId: data.variantId,
        eventType: data.event,
        value: data.value,
      },
    });
  }

  async calculateMetrics(testId: string): Promise<MetricsResult> {
    const metrics = await this.prisma.aBTestMetric.groupBy({
      by: ["variantId"],
      where: { testId },
      _count: true,
      _sum: {
        value: true,
      },
    });

    return metrics.reduce(
      (acc, metric) => ({
        ...acc,
        [metric.variantId]: {
          conversion_rate: (metric._sum.value || 0) / metric._count,
          sample_size: metric._count,
        },
      }),
      {}
    );
  }

  async calculateSignificance(testId: string): Promise<SignificanceResult> {
    // Implement statistical significance calculation
    // This is a simplified example
    return {
      pValue: 0.05,
      significant: true,
      confidenceInterval: {
        lower: 0.1,
        upper: 0.2,
      },
    };
  }

  async getTimeSeriesMetrics(
    testId: string,
    startDate: Date,
    endDate: Date,
    interval: string
  ): Promise<TimeSeriesMetrics> {
    const metrics = await this.prisma.aBTestMetric.findMany({
      where: {
        testId,
        timestamp: {
          gte: startDate,
          lte: endDate,
        },
      },
      orderBy: {
        timestamp: "asc",
      },
    });

    // Process metrics into time series format
    return {
      dates: [],
      metrics: {
        control: [],
        variant_a: [],
      },
    };
  }

  async getSegmentMetrics(
    testId: string,
    segments: Record<string, string[]>
  ): Promise<SegmentMetrics> {
    // Return segmented metrics analysis
    return {
      userType: {
        new: {},
        returning: {},
      },
      device: {
        mobile: {},
        desktop: {},
      },
    };
  }
}

interface ConversionEvent {
  testId: string;
  variantId: string;
  sessionId: string;
  event: string;
  value: number;
}

interface MetricsResult {
  [variantId: string]: {
    conversion_rate: number;
    sample_size: number;
  };
}

interface SignificanceResult {
  pValue: number;
  significant: boolean;
  confidenceInterval: {
    lower: number;
    upper: number;
  };
}

interface TimeSeriesMetrics {
  dates: Date[];
  metrics: {
    [variantId: string]: number[];
  };
}

interface SegmentMetrics {
  [segmentType: string]: {
    [segmentValue: string]: {
      conversion_rate?: number;
      sample_size?: number;
      confidence_interval?: {
        lower: number;
        upper: number;
      };
    };
  };
}

import type { ElasticsearchService } from "@/lib/cortex/elasticsearch/services";
import type { MetricsService } from "@/lib/cortex/monitoring/metrics";
import { SearchEvent } from "@/lib/shared/database/validation/generated";
import type { Logger } from "@/lib/shared/types";
import type { PrismaClient } from "@prisma/client";
import {
  LearningEvent,
  LearningEventType,
  LearningPattern,
} from "@prisma/client";
import type { SearchFeedbackData } from "./service";

/**
 * ‚öôÔ∏è Automated Processor Configuration
 *
 * Everything our automated helper needs to do its job.
 * Like giving a robot all its tools and instructions! ü§ñ
 *
 * @interface AutomatedProcessorConfig
 * @property {Logger} logger - Takes notes about what's happening
 * @property {PrismaClient} prisma - Stores and retrieves data
 * @property {ElasticsearchService} elasticsearch - Handles search operations
 * @property {MetricsService} metrics - Tracks performance
 */
interface AutomatedProcessorConfig {
  logger: Logger;
  prisma: PrismaClient;
  elasticsearch: ElasticsearchService;
  metrics: MetricsService;
}

/**
 * üìä Feedback Metrics: How Well Search is Working
 *
 * A collection of numbers that tell us if search is doing a good job.
 * Like a report card for our search system! üìà
 *
 * @interface FeedbackMetrics
 * @property {number} clickThroughRate - How often people click results
 * @property {number} averageRelevance - How good the results usually are
 * @property {number} conversionRate - How often people find what they need
 * @property {boolean} requiresAdjustment - If we need to make changes
 */
interface FeedbackMetrics {
  clickThroughRate: number;
  averageRelevance: number;
  conversionRate: number;
  requiresAdjustment: boolean;
}

/**
 * ü§ñ Automated Feedback Processor: Your Smart Search Assistant!
 *
 * This service automatically improves search based on how people use it.
 * Like having a robot librarian that learns from experience! üìö
 *
 * Features:
 * - üìä Analyzes user feedback
 * - üîÑ Makes real-time adjustments
 * - üìà Tracks performance metrics
 * - üéØ Improves search accuracy
 * - üîß Auto-tunes search settings
 *
 * @class AutomatedFeedbackProcessor
 */
export class AutomatedFeedbackProcessor {
  private readonly logger: Logger;
  private readonly prisma: PrismaClient;
  private readonly elasticsearch: ElasticsearchService;
  private readonly metrics: MetricsService;

  /**
   * üéí Sets Up Your Smart Assistant
   *
   * Gets the automated processor ready to help improve search.
   * Like booting up your helpful robot friend! ü§ñ
   *
   * @param {AutomatedProcessorConfig} config - Everything needed to work
   */
  constructor(config: AutomatedProcessorConfig) {
    this.logger = config.logger;
    this.prisma = config.prisma;
    this.elasticsearch = config.elasticsearch;
    this.metrics = config.metrics;
  }

  /**
   * ‚ö° Processes Feedback in Real-Time
   *
   * Quickly learns from how people are using search right now.
   * Like having a librarian who instantly learns from every visitor! üìö
   *
   * @param {string} searchId - Which search to analyze
   * @throws {Error} If something goes wrong during processing
   */
  async processRealTimeFeedback(searchId: string): Promise<void> {
    try {
      const recentFeedback = await this.prisma.searchEvent.findMany({
        where: {
          id: searchId,
          timestamp: {
            gte: new Date(Date.now() - 24 * 60 * 60 * 1000),
          },
        },
        select: {
          id: true,
          query: true,
          timestamp: true,
          filters: true,
          sessionId: true,
          searchType: true,
          totalHits: true,
          took: true,
          facetsUsed: true,
          resultIds: true,
          page: true,
          pageSize: true,
        },
      });

      const feedbackData = this.mapPrismaToFeedbackData(recentFeedback);
      const metrics = this.calculateFeedbackMetrics(feedbackData);

      if (metrics.requiresAdjustment) {
        await this.adjustWeights(searchId, metrics);
      }

      this.metrics.updateSearchQuality({
        relevance: metrics.averageRelevance,
        conversion_rate: metrics.conversionRate,
        click_through_rate: metrics.clickThroughRate,
      });
    } catch (error) {
      this.logger.error("Failed to process real-time feedback", {
        error,
        searchId,
      });
      throw error;
    }
  }

  /**
   * üìä Calculates Performance Metrics
   *
   * Crunches numbers to see how well search is working.
   * Like getting a report card for our search system! üìà
   *
   * @private
   * @param {SearchFeedbackData[]} feedback - User feedback to analyze
   * @returns {FeedbackMetrics} How well search is performing
   */
  private calculateFeedbackMetrics(
    feedback: SearchFeedbackData[]
  ): FeedbackMetrics {
    if (feedback.length === 0) {
      return {
        clickThroughRate: 0,
        averageRelevance: 0,
        conversionRate: 0,
        requiresAdjustment: false,
      };
    }

    const clicks = feedback.filter((f) => f.userAction === "clicked").length;
    const conversions = feedback.filter(
      (f) => f.userAction === "converted"
    ).length;
    const totalRelevance = feedback.reduce(
      (sum, f) => sum + f.relevanceScore,
      0
    );
    const total = feedback.length;

    const metrics = {
      clickThroughRate: clicks / total,
      averageRelevance: totalRelevance / total,
      conversionRate: conversions / total,
      requiresAdjustment: false,
    };

    metrics.requiresAdjustment =
      metrics.clickThroughRate < 0.2 || metrics.averageRelevance < 0.5;

    return metrics;
  }

  /**
   * üîß Adjusts Search Settings
   *
   * Changes how search works based on performance metrics.
   * Like fine-tuning a machine to work better! ‚öôÔ∏è
   *
   * @private
   * @param {string} searchId - Which search to adjust
   * @param {FeedbackMetrics} metrics - Current performance numbers
   * @throws {Error} If adjustments can't be made
   */
  private async adjustWeights(
    searchId: string,
    metrics: FeedbackMetrics
  ): Promise<void> {
    try {
      const currentWeights =
        await this.elasticsearch.getSearchWeights(searchId);
      const adjustedWeights = this.computeWeightAdjustments(
        currentWeights,
        metrics
      );

      await this.elasticsearch.updateWeights(searchId, adjustedWeights);

      this.logger.info("Search weights adjusted", {
        searchId,
        metrics,
        adjustedWeights,
      });
    } catch (error) {
      this.logger.error("Failed to adjust weights", {
        error,
        searchId,
        metrics,
      });
      throw error;
    }
  }

  /**
   * üîÑ Converts Database Data to Feedback
   *
   * Transforms raw database records into useful feedback data.
   * Like translating notes into actionable insights! üìù
   *
   * @private
   * @param {Prisma.SearchEvent | null} prismaFeedback - Raw database records
   * @returns {SearchFeedbackData[]} Processed feedback data
   */
  private mapPrismaToFeedbackData(
    prismaFeedback: SearchEvent[] | null
  ): SearchFeedbackData[] {
    if (!prismaFeedback) return [];

    return prismaFeedback.map((feedback) => {
      const filters = feedback.filters as Record<string, unknown>;
      return {
        searchId: feedback.id,
        queryHash: feedback.id,
        resultId: feedback.id,
        relevanceScore: (filters.relevanceScore as number) || 0,
        userAction:
          (filters.userAction as SearchFeedbackData["userAction"]) || "ignored",
        metadata: filters,
      };
    });
  }

  /**
   * ‚öñÔ∏è Calculates New Search Settings
   *
   * Figures out how to adjust search to work better.
   * Like a chef adjusting a recipe based on taste tests! üë®‚Äçüç≥
   *
   * @private
   * @param {Record<string, number>} currentWeights - Current search settings
   * @param {FeedbackMetrics} metrics - How well search is working
   * @returns {Record<string, number>} Adjusted search settings
   */
  private computeWeightAdjustments(
    currentWeights: Record<string, number>,
    metrics: FeedbackMetrics
  ): Record<string, number> {
    const adjustments = { ...currentWeights };

    if (metrics.clickThroughRate < 0.2) {
      adjustments.title = (currentWeights.title || 1) * 1.2;
    }

    if (metrics.averageRelevance < 0.5) {
      adjustments.content = (currentWeights.content || 1) * 1.5;
    }

    return adjustments;
  }

  async analyzeFeedback(events: LearningEvent[]): Promise<LearningPattern[]> {
    const feedbackEvents = events.filter(
      (e) => e.type === LearningEventType.USER_FEEDBACK
    );

    const patterns: LearningPattern[] = feedbackEvents.map((event) => ({
      id: `feedback_${event.id}`,
      type: "USER_FEEDBACK",
      confidence: 0.8,
      eventId: event.id,
      features: JSON.stringify({
        relevantHits:
          event.metadata && typeof event.metadata === "object"
            ? (event.metadata as Record<string, unknown>).relevantHits
            : null,
        totalHits:
          event.metadata && typeof event.metadata === "object"
            ? (event.metadata as Record<string, unknown>).totalHits
            : null,
      }),
      metadata: JSON.stringify({
        source: "feedback_analysis",
        detectedAt: new Date().toISOString(),
      }),
      createdAt: new Date(),
      updatedAt: new Date(),
    }));

    return patterns;
  }
}

import { AnalyticsMetrics } from "@/lib/cortex/analytics/types";
import type { SearchFeedbackData } from "@/lib/cortex/feedback/service";
import { MetricsService as NousMetricsService } from "@/lib/cortex/monitoring/metrics";
