}

export class MetricsService implements NousMetricsService {
  private readonly logger: Logger;
  private readonly registry: Registry;
  private readonly errorCounter: Counter<string>;
  private readonly operationLatency: Gauge<string>;
  private readonly abTestMetrics: Gauge<string>;
  private readonly analyticsMetrics: Gauge<string>;
  private readonly searchFeedback: Counter<string>;
  private metrics = {
    averageLatency: 0,
    throughput: 0,
    errorRate: 0,
    cpuUsage: 0,
    memoryUsage: 0,
    totalOperations: 0,
    successfulOperations: 0,
    failedOperations: 0,
    pendingOperations: 0,
  };
  public sampleRate: number = 1.0;
  public batchSize: number = 100;
  private baselineLoad = 0;
  private currentLoad = 0;

  public recordEngineMetric = async (data: {
    type: MetricType;
    value: number;
    confidence: number;
    metadata?: Record<string, unknown>;
    operationId?: string;
  }): Promise<void> => {
    try {
      if (typeof data.value !== 'number') {
        throw new Error('Invalid metric value');
      }

      this.analyticsMetrics
        .labels(data.type, String(data.value), data.operationId || "default")
        .set(data.value);

      this.logger.debug('Recording engine metric', data);
    } catch (error) {
      this.logger.error('Failed to record engine metric', {
        error,
        metric: data
      });
    }
  };

  public recordLearningMetrics = (
    data: {
      type: MetricType;
      value: number;
      interval: string;
      sessionId?: string;
      modelId?: string;
      metadata?: Record<string, unknown>;
    }
  ): Promise<void> => {
    this.analyticsMetrics
      .labels(data.type, String(data.value), data.interval)
      .set(data.value);
    return Promise.resolve();
  };

  constructor({ logger, environment }: { logger: Logger; environment: string }) {
    this.logger = logger;
    this.registry = new Registry();

    // Initialize default metrics collection
    collectDefaultMetrics({
      register: this.registry,
      prefix: metricsConfig.prefix,
      labels: { ...metricsConfig.defaultLabels },
      gcDurationBuckets: [0.001, 0.01, 0.1, 1, 2, 5],
    });

    this.errorCounter = new Counter({
      name: "sophra_errors_total",
      help: "Total number of errors",
      labelNames: ["type", "service", "operation"],
      registers: [this.registry],
    });

    this.operationLatency = new Gauge({
      name: "sophra_operation_latency",
      help: "Operation latency in milliseconds",
      labelNames: ["operation", "service", "value"],
      registers: [this.registry],
    });

    this.searchFeedback = new Counter({
      name: "sophra_search_feedback",
      help: "Search feedback metrics",
      labelNames: ["user_action", "query_hash", "relevance_score"],
      registers: [this.registry],
    });

    this.abTestMetrics = new Gauge({
      name: "sophra_abtest_metrics",
      help: "A/B test metrics",
      labelNames: ["test_id", "variant_id", "query_hash", "metric_type"],
      registers: [this.registry],
    });

    this.analyticsMetrics = new Gauge({
      name: "sophra_analytics_metrics",
      help: "Analytics metrics",
      labelNames: ["metric_type", "value", "time_window"],
      registers: [this.registry],
    });
  }
  updateSearchQuality(metrics: {
    relevance: number;
    conversion_rate: number;
    click_through_rate?: number;
    query_hash?: string;
  }): void {
    const queryHash = metrics.query_hash || "current";

    this.analyticsMetrics
      .labels("search_relevance", String(metrics.relevance), queryHash)
      .set(metrics.relevance);

    this.analyticsMetrics
      .labels("conversion_rate", String(metrics.conversion_rate), queryHash)
      .set(metrics.conversion_rate);

    if (metrics.click_through_rate !== undefined) {
      this.analyticsMetrics
        .labels("click_through_rate", String(metrics.click_through_rate), queryHash)
        .set(metrics.click_through_rate);
    }

    this.logger.debug('Updating search quality metrics', {
      ...metrics,
      query_hash: queryHash
    });
  }
  recordAlert(
    metric: string,
    data: {
      threshold: number;
      value: number;
      actual: number;
      severity: "info" | "warning" | "critical";
    }
  ): void {
    this.analyticsMetrics
      .labels(`${metric}_alert`, String(data.actual), data.severity)
      .set(data.actual);

    this.analyticsMetrics
      .labels(`${metric}_threshold`, String(data.threshold), data.severity)
      .set(data.threshold);

    this.logger.debug('Recording metric alert', {
      metric,
      ...data
    });
  }
  recordReportDistribution(params: {
    report_type: string;
    recipient_count: number;
    type: string;
    timeWindow: string;
  }): void {
    this.analyticsMetrics
      .labels(
        "report_distribution",
        String(params.recipient_count),
        params.timeWindow
      )
      .set(params.recipient_count);

    this.logger.debug('Recording report distribution', {
      ...params
    });
  }

  public updateCacheHitRatio(hits: number, misses: number): void {
    const total = hits + misses;
    if (total > 0) {
      const ratio = hits / total;
      this.analyticsMetrics
        .labels("cache_hit_ratio", ratio.toString(), "1m")
        .set(ratio);
      this.logger.debug("Updating cache hit ratio", { hits, misses, ratio });
    }
  }

  public incrementError(type: string, service: string, operation: string): void {
    this.errorCounter.labels(type, service, operation).inc();
    this.logger.debug('Incrementing error counter', {
      error: type,
      service,
      operation
    });
  }

  public recordLatency(
    operation: string,
    service: string,
    value: number
  ): void {
    this.operationLatency
      .labels(operation, service, String(value))
      .set(value);

    this.logger.debug('Recording latency', {
      operation,
      service,
      latency: value
    });
  }

  public observeSearchFeedback(feedback: SearchFeedbackData): void {
    this.searchFeedback
      .labels(
        feedback.userAction,
        feedback.queryHash,
        feedback.relevanceScore.toString()
      )
      .inc();
  }

  public incrementSearchError(params: ElasticsearchErrorParams): void {
    this.incrementError(
      params.error_type,
      "search",
      params.search_type
        ? `${params.search_type}_search_${params.index}`
        : `search_${params.index}`
    );
  }

  public incrementIndexError(params: ElasticsearchErrorParams): void {
    this.incrementError(
      params.error_type,
      "elasticsearch",
      `index_${params.index}`
    );
  }

  public incrementUpdateError(params: ElasticsearchErrorParams): void {
    this.incrementError(
      params.error_type,
      "elasticsearch",
      `update_${params.index}`
    );
  }

  public incrementDeleteError(params: ElasticsearchErrorParams): void {
    this.incrementError(
      params.error_type,
      "elasticsearch",
      `delete_${params.index}`
    );
  }

  public incrementBulkError(params: ElasticsearchErrorParams): void {
    this.incrementError(
      params.error_type,
      "elasticsearch",
      `bulk_${params.index}`
    );
  }

  public updateABTestMetrics(params: {
    test_id: string;
    variant_id: string;
    query_hash: string;
    metrics: Record<string, number>;
  }): void {
    const { test_id, variant_id, query_hash, metrics } = params;
    Object.entries(metrics).forEach(([metric_type, value]) => {
      this.abTestMetrics
        .labels(test_id, variant_id, query_hash, metric_type)
        .set(value);
    });
  }
  public updateAnalytics(metrics: AnalyticsMetrics): void {
    const { timeWindow = "hourly", ...rawMetrics } = metrics;

    // Normalize metrics keys to snake_case for consistency
    const normalizedMetrics = Object.entries(rawMetrics).reduce(
      (acc, [key, value]) => {
        const snakeKey = key.replace(
          /[A-Z]/g,
          (letter) => `_${letter.toLowerCase()}`
        );
        acc[snakeKey] = typeof value === "number" ? value : 0;
        return acc;
      },
      {} as Record<string, number>
    );

    Object.entries(normalizedMetrics).forEach(([metric_type, value]) => {
      this.analyticsMetrics
        .labels(metric_type, String(value), String(timeWindow))
        .set(value);
    });
  }

  public async getMetrics(): Promise<string> {
    return await this.registry.metrics();
  }

  public incrementMetric(
    name: string,
    labels: Record<string, string | number>
  ): void {
    const normalizedLabels = Object.entries(labels).reduce(
      (acc, [key, value]) => {
        acc[key.replace(/([A-Z])/g, "_$1").toLowerCase()] = String(value);
        return acc;
      },
      {} as Record<string, string>
    );

    this.errorCounter
      .labels(
        normalizedLabels.type || "unknown",
        normalizedLabels.service || "unknown",
        normalizedLabels.operation || name
      )
      .inc();
  }

  public updateResourceUsage(metrics: ResourceMetrics): void {
    try {
      const { memory, cpu } = metrics;

      if (!memory || !cpu || typeof memory.used !== 'number' || typeof memory.total !== 'number' || typeof cpu.usage !== 'number') {
        throw new Error('Invalid resource metrics');
      }

      this.analyticsMetrics
        .labels("memory_used", String(memory.used), "current")
        .set(memory.used);

      this.analyticsMetrics
        .labels("memory_total", String(memory.total), "current")
        .set(memory.total);

      this.analyticsMetrics
        .labels("cpu_usage", String(cpu.usage), "current")
        .set(cpu.usage);

      this.logger.debug('Updating resource usage metrics', {
        cpuUsage: cpu.usage,
        memoryUsage: memory.used,
        memoryTotal: memory.total
      });
    } catch (error) {
      this.logger.error('Failed to update resource metrics', {
        error,
        metrics
      });
    }
  }

  public hasMetric(name: string): boolean {
    return this.registry.getSingleMetric(name) !== undefined;
  }

  public createHistogram(name: string, help: string): void {
    if (!this.hasMetric(name)) {
      new Counter({
        name: `sophra_${name}`,
        help,
        labelNames: ["operation", "service"],
        registers: [this.registry],
      });
    }
  }

  public createCounter(name: string, help: string): void {
    if (!this.hasMetric(name)) {
      new Counter({
        name: `sophra_${name}`,
        help,
        labelNames: ["type", "service", "operation"],
        registers: [this.registry],
      });
    }
  }

  public static async getEngineMetrics(): Promise<EngineMetrics> {
    return {
      averageLatency: 0,
      requestsPerSecond: 0,
      errorRate: 0,
      cpuUsage: 0,
      memoryUsage: 0,
      totalOperations: 0,
      successfulOperations: 0,
      failedOperations: 0,
      pendingOperations: 0,
    };
  }

  async getAverageLatency(): Promise<number> {
    return this.metrics.averageLatency;
  }

  async getThroughput(): Promise<number> {
    return this.metrics.throughput;
  }

  async getErrorRate(): Promise<number> {
    return this.metrics.errorRate;
  }

  async getCPUUsage(): Promise<number> {
    return this.metrics.cpuUsage;
  }

  async getMemoryUsage(): Promise<number> {
    return this.metrics.memoryUsage;
  }

  async getOperationCount(): Promise<number> {
    return this.metrics.totalOperations;
  }

  async getSuccessfulOperations(): Promise<number> {
    return this.metrics.successfulOperations;
  }

  async getFailedOperations(): Promise<number> {
    return this.metrics.failedOperations;
  }

  async getPendingOperations(): Promise<number> {
    return this.metrics.pendingOperations;
  }

  async updateMetrics(update: Partial<typeof this.metrics>): Promise<void> {
    this.metrics = { ...this.metrics, ...update };
    this.logger.debug("Updated metrics", { metrics: this.metrics });
  }

  public getCurrentLoad(): number {
    return this.currentLoad;
  }

  public getBaselineLoad(): number {
    return this.baselineLoad;
  }

  public async getMetricVariability(): Promise<number> {
    const latencyValues = await this.getLatencyHistory();
    if (!latencyValues.length) return 0.1; // default variability

    const mean =
      latencyValues.reduce((sum, val) => sum + val, 0) / latencyValues.length;
    const variance =
      latencyValues.reduce((sum, val) => sum + Math.pow(val - mean, 2), 0) /
      latencyValues.length;
    return Math.sqrt(variance) / mean; // coefficient of variation
  }

  public async getAverageTrafficVolume(): Promise<number> {
    return this.metrics.throughput;
  }

  private async getLatencyHistory(): Promise<number[]> {
    const latencyMetric = this.registry.getSingleMetric(
      "sophra_operation_latency"
    );
    if (!latencyMetric) return [];

    return (await (latencyMetric as Gauge<string>).get()).values.map(
      (v) => v.value
    );
  }

  public async getMetricsForVariant(
    _variant: string,
    startTime: number
  ): Promise<TestMetrics> {
    return {
      latency: await this.getAverageLatency(),
      errorRate: await this.getErrorRate(),
      throughput: await this.getThroughput(),
      cpuUsage: await this.getCPUUsage(),
      memoryUsage: await this.getMemoryUsage(),
    };
  }
}

import type { ElasticsearchService } from "@/lib/cortex/elasticsearch/services";
import { SearchABTestingService } from "@/lib/cortex/feedback/ab-testing";
import { AutomatedFeedbackProcessor } from "@/lib/cortex/feedback/automated-processor";
import { MetricsService } from "@/lib/cortex/monitoring/metrics";
import { prisma } from "@/lib/shared/database/client";
import type { Logger } from "@/lib/shared/types";
import { JsonValue } from "@prisma/client/runtime/library";
/**
 * üìù Search Feedback Data: What Users Think About Search Results
 *
 * This is like a report card from users about how well our search is working.
 * It helps us understand if people are finding what they need! üìä
 *
 * @interface SearchFeedbackData
 * @property {string} searchId - Which search this feedback is for
 * @property {string} queryHash - A unique ID for the search query
 * @property {string} resultId - Which result they're giving feedback on
 * @property {number} relevanceScore - How relevant the result was (like a grade!)
 * @property {'clicked' | 'ignored' | 'converted'} userAction - What the user did
 * @property {Record<string, unknown>} metadata - Extra details about the feedback
 */
interface SearchMetadata {
  testId?: string;
  variantId?: string;
  [key: string]: unknown;
}

interface SearchMetrics {
  clickThroughRate: number;
  averageRelevance: number;
  conversionRate: number;
}

interface ABTestMetrics {
  relevance: number;
  action: 'clicked' | 'ignored' | 'converted';
}

export interface SearchFeedbackData {
  searchId: string;
  queryHash: string;
  resultId: string;
  relevanceScore: number;
  userAction: "clicked" | "ignored" | "converted";
  metadata: SearchMetadata;
}

/**
 * üìä Feedback Statistics: The Big Picture of User Feedback
 *
 * A summary of how well our search is performing based on user actions.
 * Like a report card for our entire search system! üìà
 *
 * @interface FeedbackStats
 * @property {number} clickThroughRate - How often users click results
 * @property {number} averageRelevance - How relevant results usually are
 * @property {number} conversionRate - How often users find what they need
 */
interface FeedbackStats {
  clickThroughRate: number;
  averageRelevance: number;
  conversionRate: number;
}

/**
 * üéØ Feedback Service: Your Search Quality Guardian!
 *
 * This service helps us understand and improve search quality based on user feedback.
 * Think of it as a teacher collecting and analyzing student feedback to make the class better! üìö
 *
 * Features:
 * - üìù Collects user feedback
 * - üìä Calculates statistics
 * - ü§ñ Processes feedback automatically
 * - üî¨ Runs A/B tests
 * - üìà Tracks metrics
 *
 * Works with:
 * - üóÑÔ∏è Database (Prisma)
 * - üìù Logger
 * - üîç Elasticsearch
 * - üìä Metrics Service
 * - ü§ñ Automated Processor
 * - üî¨ A/B Testing Service
 */
export class FeedbackService {
  private readonly prisma: typeof prisma;
  private readonly logger: Logger;
  private readonly metrics: MetricsService;
  private readonly automatedProcessor: AutomatedFeedbackProcessor;
  private readonly abTesting: SearchABTestingService;

  /**
   * üéí Sets Up the Feedback Collection System
   *
   * Gets everything ready to collect and process user feedback.
   * Like setting up suggestion boxes all around the library! üì´
   *
   * @param {Object} config - Everything we need to get started
   * @param {PrismaClient} config.prisma - Our database connection
   * @param {Logger} config.logger - Our note-taker
   * @param {ElasticsearchService} config.elasticsearch - Our search service
   */
  constructor(config: {
    prisma: typeof prisma;
    logger: Logger;
    elasticsearch: ElasticsearchService;
  }) {
    this.prisma = config.prisma;
    this.logger = config.logger;
    this.metrics = new MetricsService({ 
      logger: this.logger,
      environment: process.env.NODE_ENV || 'development'
    });
    this.automatedProcessor = new AutomatedFeedbackProcessor({
      logger: this.logger,
      prisma: this.prisma,
      elasticsearch: config.elasticsearch,
      metrics: this.metrics,
    });
    this.abTesting = new SearchABTestingService({
      logger: this.logger,
      prisma: this.prisma,
      metrics: this.metrics,
    });
  }

  /**
   * üìä Calculates Feedback Statistics
   *
   * Crunches the numbers to see how well our search is doing.
   * Like a teacher grading tests to see how well the class is learning! üìö
   *
   * @private
   * @param {PrismaClient['searchEvent']['payload']['default'][]} feedback - All the feedback to analyze
   * @returns {FeedbackStats} A summary of how we're doing
   */
  private calculateFeedbackStats(
    feedback: Array<{
      id: string;
      filters: JsonValue;
    }>
  ): FeedbackStats {
    const total = feedback.length;
    if (total === 0) {
      return { clickThroughRate: 0, averageRelevance: 0, conversionRate: 0 };
    }

    const clicks = feedback.filter((f) => {
      const filters = f.filters as Record<string, unknown>;
      return filters?.userAction === "clicked";
    }).length;

    return {
      clickThroughRate: clicks / total,
      averageRelevance: this.calculateAverageRelevance(feedback),
      conversionRate: this.calculateConversionRate(feedback),
    };
  }

  /**
   * üìù Records User Feedback
   *
   * Saves what users think about search results.
   * Like keeping a diary of what works and what doesn't! üìñ
   *
   * @param {SearchFeedbackData} feedback - What the user thought
   * @throws {Error} If something goes wrong while saving
   */
  async recordFeedback(feedback: SearchFeedbackData): Promise<void> {
    // Validate feedback data
    if (feedback.relevanceScore < 0 || feedback.relevanceScore > 1) {
      throw new Error('Invalid feedback data: relevance score must be between 0 and 1');
    }

    try {
      await this.prisma.searchEvent.update({
        where: { id: feedback.searchId },
        data: {
          filters: {
            set: {
              userAction: feedback.userAction,
              relevanceScore: feedback.relevanceScore,
              metadata: feedback.metadata,
            } as JsonValue,
          },
        },
      });

      await this.metrics.observeSearchFeedback(feedback);
    } catch (error) {
      this.logger.error('Failed to record feedback', { error, feedback });
      throw error;
    }
  }

  /**
   * üìä Calculates Average Relevance Score
   *
   * Figures out how relevant our results usually are.
   * Like getting the class average on a test! üìà
   *
   * @private
   * @param {PrismaClient['searchEvent']['payload']['default'][]} feedback - All feedback to analyze
   * @returns {number} The average relevance score
   */
  private calculateAverageRelevance(
    feedback: Array<{
      id: string;
      filters: JsonValue;
    }>
  ): number {
    if (feedback.length === 0) return 0;
    const totalRelevance = feedback.reduce((sum, f) => {
      const filters = f.filters as Record<string, unknown>;
      return sum + ((filters?.relevanceScore as number) || 0);
    }, 0);
    return totalRelevance / feedback.length;
  }

  /**
   * üéØ Calculates Success Rate
   *
   * Figures out how often users find exactly what they need.
   * Like tracking how many students pass the exam! üéì
   *
   * @private
   * @param {PrismaClient['searchEvent']['payload']['default'][]} feedback - All feedback to analyze
   * @returns {number} The conversion rate
   */
  private calculateConversionRate(
    feedback: Array<{
      id: string;
      filters: JsonValue;
    }>
  ): number {
    if (feedback.length === 0) return 0;
    const conversions = feedback.filter((f) => {
      const filters = f.filters as Record<string, unknown>;
      return filters?.userAction === "converted";
    }).length;
    return conversions / feedback.length;
  }

  /**
   * üöÄ Records Feedback and Optimizes Search
   *
   * Not only saves feedback but also uses it to make search better right away!
   * Like a teacher adjusting their teaching style based on student questions! üìö
   *
   * @param {SearchFeedbackData} feedback - What the user thought
   * @throws {Error} If something goes wrong during processing
   */
  async recordFeedbackWithOptimization(
    feedback: SearchFeedbackData & { testData?: { variantId?: string } }
  ): Promise<void> {
    // Validate test data first if present
    if (feedback.metadata?.testId && !feedback.metadata?.variantId) {
      throw new Error('Invalid test data: missing variantId');
    }

    this.logger.debug('Recording feedback with optimization', { feedback });

    // Record the feedback first
    await this.recordFeedback(feedback);

    // Track A/B test metrics if test data is present
    const metadata = feedback.metadata as SearchMetadata;
    if (metadata.testId && metadata.variantId) {
      // Calculate metrics based on user action and relevance score
      const clickThroughRate = feedback.userAction === 'clicked' ? 1 : 0;
      const conversionRate = feedback.userAction === 'converted' ? 1 : 0;

      await this.abTesting.trackVariantMetrics({
        testId: metadata.testId,
        variantId: metadata.variantId,
        queryHash: feedback.queryHash,
        metrics: {
          clickThroughRate,
          averageRelevance: feedback.relevanceScore,
          conversionRate
        }
      });
    }

    // Trigger automated optimization
    try {
      await this.automatedProcessor.processRealTimeFeedback(feedback.queryHash);
    } catch (error) {
      this.logger.error('Failed to process feedback for optimization', { error });
      throw error;
    }
  }
}
