export * from './base-engine';
export * from './learning-engine';
export * from './adaptation-engine';
export type { PatternDetectionResult } from './adaptation-engine';
export * from './processors';

import { Logger } from "@/lib/shared/types";
import { BaseProcessor } from "./processors";

export class BaseEngine {
  protected processors: BaseProcessor[] = [];

  constructor(protected logger: Logger) {}

  registerProcessor(processor: BaseProcessor): void {
    this.processors.push(processor);
  }

  unregisterProcessor(processor: BaseProcessor): void {
    const index = this.processors.indexOf(processor);
    if (index !== -1) {
      this.processors.splice(index, 1);
    }
  }

  async run(): Promise<void> {
    for (const processor of this.processors) {
      try {
        await processor.process();
      } catch (error) {
        console.error("Error running processor:", error);
      }
    }
  }
}

import { prisma } from '../database/client';
import { Logger } from '../types';
import {
  EngineOperation,
  EngineOperationStatus,
  EngineOperationType,
  EngineOptimizationStrategy,
  LearningEvent,
  LearningPattern,
} from "@prisma/client";
import { BaseEngine } from "./base-engine";
import {
  FeedbackProcessor,
  PerformanceProcessor,
  StrategyProcessor,
  TimeBasedProcessor,
} from "./processors";
import type { ITimeBasedProcessor } from "./processors/time-based-processor";

export interface PatternDetectionResult {
  operation: EngineOperation;
  patterns: LearningPattern[];
}

export interface ProcessorMap {
  feedback: FeedbackProcessor;
  performance: PerformanceProcessor;
  timeBased: ITimeBasedProcessor;
  strategy: StrategyProcessor;
}

export class LearningEngine extends BaseEngine {
  private prisma: typeof prisma;
  private processorMap: ProcessorMap;

  constructor(
    logger: Logger, 
    processorMap: ProcessorMap,
    prismaClient: typeof prisma = prisma
  ) {
    super(logger);
    this.prisma = prismaClient;
    this.processorMap = processorMap;
    // Register processors with base engine
    this.processors = Object.values(processorMap);
  }

  public async executeStrategy(
    strategy: EngineOptimizationStrategy
  ): Promise<void> {
    await this.processorMap.strategy.executeStrategy(strategy);
  }

  async detectPatterns(
    events: LearningEvent[]
  ): Promise<PatternDetectionResult> {
    const operation = await this.prisma.engineOperation.create({
      data: {
        type: EngineOperationType.PATTERN_DETECTION,
        status: EngineOperationStatus.RUNNING,
        startTime: new Date(),
        createdAt: new Date(),
        updatedAt: new Date(),
      },
    });

    try {
      const patterns = await Promise.all([
        this.processorMap.feedback.analyze(events),
        this.processorMap.performance.analyze(events),
        this.processorMap.timeBased.analyze(events),
      ]).then((results) => results.flat());

      const updatedOperation = await this.prisma.engineOperation.update({
        where: { id: operation.id },
        data: {
          status: EngineOperationStatus.COMPLETED,
          endTime: new Date(),
          updatedAt: new Date(),
        },
      });

      return {
        operation: updatedOperation,
        patterns,
      };
    } catch (error) {
      await this.prisma.engineOperation.update({
        where: { id: operation.id },
        data: {
          status: EngineOperationStatus.FAILED,
          error: error instanceof Error ? error.message : "Unknown error",
          endTime: new Date(),
          updatedAt: new Date(),
        },
      });
      throw error;
    }
  }

  async executeOperation(operation: EngineOperation): Promise<void> {
    try {
      switch (operation.type) {
        case EngineOperationType.PATTERN_DETECTION:
          // Pattern detection is handled by detectPatterns
          break;
        case EngineOperationType.STRATEGY_EXECUTION:
          // Strategy execution is handled by the strategy processor
          break;
        default:
          throw new Error(`Unsupported operation type: ${operation.type}`);
      }
    } catch (error) {
      this.logger.error("Failed to execute operation", { error, operation });
      throw error;
    }
  }

  async getTimeSeriesData(params: {
    startDate: Date;
    endDate: Date;
    granularity: string;
  }) {
    return this.processorMap.timeBased.getTimeSeriesData(params);
  }

  async analyzeTemporalCorrelations(timeSeriesData: any) {
    return this.processorMap.timeBased.analyzeCorrelations(timeSeriesData);
  }

  async findRecurringPatterns(params: {
    timeframe: string;
    minConfidence: number;
  }) {
    return this.processorMap.timeBased.findRecurringPatterns(params);
  }
}

import logger from "@/lib/shared/logger";
import type { Logger } from "@/lib/shared/types";
import { Action, RuleContext } from "../types";

export interface NotifyActionConfig {
  title: string;
  message: string;
  severity: "info" | "warning" | "error";
  channels?: string[];
}

export class NotifyAction implements Action {
  private config: NotifyActionConfig;
  private logger: Logger;

  constructor(config: NotifyActionConfig, logger: Logger) {
    this.config = config;
    this.logger = logger;
  }

  execute(context: RuleContext): void {
    const notification = {
      ...this.config,
      timestamp: context.timestamp,
      eventData: context.eventData,
      metrics: context.metrics,
    };

    // TODO: Implement actual notification dispatch
    this.logger.info("Would send notification:", notification);
  }
}

export class UpdateStateAction implements Action {
  private updates: Record<string, unknown>;

  constructor(updates: Record<string, unknown>) {
    this.updates = updates;
  }

  execute(context: RuleContext): void {
    Object.assign(context.systemState, this.updates);
  }
}

export class CompositeAction implements Action {
  private actions: Action[];
  private logger: Logger;

  constructor(actions: Action[], logger: Logger) {
    this.actions = actions;
    this.logger = logger;
  }

  execute(context: RuleContext): void {
    for (const action of this.actions) {
      try {
        action.execute(context);
      } catch (error) {
        this.logger.error(`Action failed:`, { error });
      }
    }
  }
}

export class ThresholdAdjustmentAction implements Action {
  private metricName: string;
  private adjustment: number;
  private minValue?: number;
  private maxValue?: number;
  private logger: Logger;

  constructor(config: {
    metricName: string;
    adjustment: number;
    minValue?: number;
    maxValue?: number;
  }, logger: Logger) {
    this.metricName = config.metricName;
    this.adjustment = config.adjustment;
    this.minValue = config.minValue;
    this.maxValue = config.maxValue;
    this.logger = logger;
  }

  execute(context: RuleContext): void {
    const current = context.metrics[this.metricName] || 0;
    let newValue = current + this.adjustment;

    if (this.minValue !== undefined) {
      newValue = Math.max(newValue, this.minValue);
    }
    if (this.maxValue !== undefined) {
      newValue = Math.min(newValue, this.maxValue);
    }

    context.metrics[this.metricName] = newValue;
    this.logger.info(`Adjusted ${this.metricName}`, {
      from: current,
      to: newValue,
    });
  }
}

